    super(props)
    this.myRef = React.createRef()
  }

  Sketch = (p) => {

     p.setup = (sketch) => {
     sketch.createCanvas(200, 200);
     }

     p.draw = (sketch) => {
    sketch.background(0);
    sketch.fill(255);
    sketch.rect(x,y,50,50);
     }
  }

  componentDidMount() {
    this.myP5 = new p5(this.Sketch, this.myRef.current)
  }

  render() {
    return (
