import React from 'react';
import ReactDOM from 'react-dom';
import Sketch from 'react-p5';


class App extends React.Component {
    y = 0;
    direction = '^';

    render() {
        return (
            <div className="App">
                <h1>react-p5</h1>
                <Sketch
                    setup={(p5, parentRef) => {
                        p5.createCanvas(200, 200).parent(parentRef);
                        p5.fill(240);
                        p5.noStroke();
                    }}

                    draw={p5 => {
                        p5.background('pink');
                        let t = p5.frameCount / 60; // update time

                        // create a random number of snowflakes each frame
                        for (let i = 0; i < p5.random(5); i++) {
                           this.nowflakes.push(new snowflake()); // append snowflake object
                        }

                        // loop through snowflakes with a for..of loop
                        for (let flake of p5.snowflakes) {
                            p5.flake.update(t); // update snowflake position
                            p5.flake.display(); // draw snowflake
                        }
                    }}


                />
            </div>
        );
    }
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App />, rootElement);
