import React from 'react';
import ReactDOM from 'react-dom';
import Sketch from 'react-p5';


class App extends React.Component {

  x = 50;
  y = 50;

  setup = (p5, canvasParentRef) => {
    p5.createCanvas(720, 400).parent(canvasParentRef); // use parent to render canvas in this ref (without that p5 render this canvas outside your component)
