import React from 'react';
import Sketch from 'react-p5';
import './style.css';

class Glitch extends React.Component {

    render() {
        var cnv;
        var wid = 500;
        var hei = 300;
        var NB_FRAMES = 100;
        var frame_count = 0;

        return (
            <div className="center">
                <h1>Glitch</h1>
                <a href="https://p5js.org/examples/color-lerp-color.html">Original Sketch reference </a>
                <hr />
                <Sketch
                    setup={(p5, parentRef) => {
                        p5.createCanvas(700, 500).parent(parentRef);
                        p5.background(255);
                        p5.noStroke();



                    }}
                    draw={p5 => {
                        p5.background(255);
                        from = color(255, 0, 0, 0.2 * 255);
                        to = color(0, 0, 255, 0.2 * 255);
                        c1 = lerpColor(from, to, 0.33);
                        c2 = lerpColor(from, to, 0.66);
                        for (let i = 0; i < 15; i++) {
                            fill(from);
                            quad(
                                p5.random(-40, 220), p5.random(p5.height),
                                p5.random(-40, 220), p5.random(p5.height),
                                p5.random(-40, 220), p5.random(p5.height),
                                p5.random(-40, 220), p5.random(p5.height)
                            );
                            fill(c1);
                            quad(
                                p5.random(140, 380), p5.random(p5.height),
                                p5.random(140, 380), p5.random(p5.height),
                                p5.random(140, 380), p5.random(p5.height),
                                p5.random(140, 380), p5.random(p5.height)
                            );
                            fill(c2);
                            quad(
                                p5.random(320, 580), p5.random(p5.height),
                                p5.random(320, 580), p5.random(p5.height),
                                p5.random(320, 580), p5.random(p5.height),
                                p5.random(320, 580), p5.random(p5.height)
                            );
                            fill(to);
                            quad(
                                p5.random(500, 760), random(p5.height),
                                p5.random(500, 760), random(p5.height),
                                p5.random(500, 760), random(p5.height),
                                p5.random(500, 760), random(p5.height)
                            );
                        }
                        frameRate(5);

                    }}
                />
            </div>
        );
    }
}

export default Glitch;
