import React from 'react';
import Sketch from 'react-p5';
import './style.css';

class Glitch extends React.Component {

    render() {
        var cnv;
        var wid = 500;
        var hei = 300;
        var NB_FRAMES = 100;
        var frame_count = 0;

        return (
            <div className="center">
                <h1>Glitch</h1>
                <a href="https://editor.p5js.org/ronicantor/sketches/eq2bIhmh2">Original Sketch reference </a>
                <hr />
                <Sketch
                    setup={(p5, parentRef) => {
                        p5.createCanvas(700, 500).parent(parentRef);
                       curSeed = 11;
    noiseSeed(curSeed);
    randomSeed(1);

   createCanvas(wid,hei,SVG);
      strokeWeight(1);   // do 0.1 for laser
    stroke(255,0,0);      // red is good for laser
  noFill();
    //cnv.parent("canvas");

    background(0);

    for(var i = 0;i<NB;i++) {
        Objects[i] = new object(i);

}
             // better not to have a fill for laser



                    }}
                    draw={p5 => {
                        p5.background('#8B0000');
                        let t = p5.frameCount / 60; // update time

                        // create a random number of snowflakes each frame
                        for (let i = 0; i < p5.random(5); i++) {
                            snowflakes.push(new snowflake()); // append snowflake object
                        }

                        // loop through snowflakes with a for..of loop
                        for (let flake of snowflakes) {
                            flake.update(t); // update snowflake position
                            flake.display(); // draw snowflake
                        }

                        // snowflake class
                        function snowflake() {
                            // initialize coordinates
                            this.posX = 0;
                            this.posY = p5.random(-50, 0);
                            this.initialangle = p5.random(0, 2 * p5.PI);
                            this.size = p5.random(2, 5);

                            // radius of snowflake spiral
                            // chosen so the snowflakes are uniformly spread out in area
                            this.radius = p5.sqrt(p5.random(p5.pow(p5.width / 2, 2)));

                            this.update = function (time) {
                                // x position follows a circle
                                let w = 0.6; // angular speed
                                let angle = w * time + this.initialangle;
                                this.posX = p5.width / 2 + this.radius * p5.sin(angle);

                                // different size snowflakes fall at slightly different y speeds
                                this.posY += p5.pow(this.size, 0.5);

                                // delete snowflake if past end of screen
                                if (this.posY > p5.height) {
                                    let index = snowflakes.indexOf(this);
                                    snowflakes.splice(index, 1);
                                }
                            };

                            this.display = function () {
                                p5.ellipse(this.posX, this.posY, this.size);
                            };
                        }

                    }}
                />
            </div>
        );
    }
}

export default Glitch;
